import React from 'react';
import axios from 'axios';
import { Card, Row, Col, List, message, Avatar, Spin } from "antd";

const gridStyle = {
    textAlign: 'center',
  };

class AuthorDetail extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            FA: "Fantasy",
            NF: "Non Fiction",
            RO: "Romance",
            TR: "Thriller",
            MY: "Mystery",
            BI: "Biography",
            FI: "Fiction",
            SF: "Science Fiction"
        };
    }  

    componentDidMount = () => {
        const authID = this.props.match.params.authID;
        this._isMounted=true;
        axios.get(`http://127.0.0.1:8000/library/author/${authID}`).then(res => {
          this.setState({
            // book: res.data
            title: res.data.name,
            birthdate: res.data.birthdate,
            numFollowers: res.data.numFollowers,
            image_url: res.data.image_url,
            genre: this.state[res.data.genre],
            rating: res.data.review,
            number_of_reviews: res.data.review_count,
            synopsis: res.data.bio
          });
        })
        .catch(error => console.log(error));
    }  

    componentWillUnmount(){
        this._isMounted = false;
        console.log("Unmounting");
    }

    render() {
        return (
                <div>
                    <Row gutter={20} type="flex" justify="center">
                        <Col span={6}>
                            <Card bodyStyle={{
                                padding: 0
                            }}>
                                <img src={this.state.image_url} style={{    
                                }}
                                width="100%" height="100%" >
                                </img>
                            </Card>
                        </Col>

                        <Col span={16}>
                            <Card title={this.state.title} headStyle={{
                                fontSize: 20,
                                fontStyle: 'italic',
                                fontFamily: 'Georgia'
                            }}>
                                <p>
                                    <b><i>Birthdate: </i></b> 
                                    {this.state.birthdate}
                                </p>

                                <p>
                                    <b><i>Number of Followers: </i></b> 
                                    {this.state.numFollowers}
                                </p>

                                <p>
                                    <b><i>Genre: </i></b>
                                    {this.state.genre}
                                </p>

                                <p>
                                    <b><i>Rating: </i></b>
                                    {this.state.rating}
                                </p>

                                <p>
                                    <b><i>Number of reviews: </i></b>
                                    {this.state.number_of_reviews}
                                </p>
                            </Card>
                        </Col>

                    </Row>

                    <Row gutter={20} type="flex" justify="center">
                        <Col span={22}>
                            <Card style={gridStyle} title="Synopsis/blurb">
                                <p>
                                    {this.state.synopsis}
                                </p>
                            </Card>
                        </Col>
                    </Row>
                </div>
        )
    }
}

class Title extends React.Component {
    render() {
        return (
            <h2>
                Title
            </h2>
        );
    }
}

export default AuthorDetail;
