import React from 'react';
import Block from 'react-blocks';
import Background from '../images/books.jpg';

const CenteredLayout = (props) => {
    return (
        <div>
            <img src={Background} alt="bg"
            style={{
                position: 'absolute',
                left: '50%',
                top: '50%',
                transform: 'translate(-50%, -50%)'
            }} />
            <div style={{
                position: 'absolute',
                left: '50%',
                top: '50%',
                transform: 'translate(-50%, -50%)',
                backgroundColor: '#000',

            }} >
                {props.children}

            </div>
        </div>
    );    
};

export default CenteredLayout;
