import React from 'react';
import axios from 'axios';
import { Card, Row, Col, List, Icon } from "antd";
import { MDBMask, MDBView, MDBContainer } from "mdbreact";

const gridStyle = {
    textAlign: 'center',
  };

class ProfileLookup extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            FA: "Fantasy",
            NF: "Non Fiction",
            RO: "Romance",
            TR: "Thriller",
            MY: "Mystery",
            BI: "Biography",
            FI: "Fiction",
            SF: "Science Fiction"
        };
    }  


    componentDidMount() {
        const profileID = this.props.match.params.profileID;
        axios.get(`http://127.0.0.1:8000/profile/user/${profileID}`).then(res => {
            this.setState({
                first_name: res.data.first_name,
                last_name: res.data.last_name,
                private: true,
                review_count: res.data.review_count,
                creation_date: res.data.creation_date,
                bio: res.data.bio,
                genre: res.data.genre,
                avatar: res.data.avatar,
                books: res.data.favorites,
                authors: res.data.following
            });

        })
        .catch(error => console.log(error));
    }

    render() {
        return (
                <div>
                    <Row gutter={20} style={{ marginBottom: 16 }} type="flex" justify="center">
                        <Col span={6}>
                            <Card bodyStyle={{
                                padding: 0
                            }}>
                                <img src={this.state.avatar} style={{
                                    width: 211, height: 221
                                }}
                                width="100%" height="100%" >
                                </img>
                            </Card>
                        </Col>

                        <Col span={16}>
                        <MDBContainer>
                        <MDBView>
                            <Card  headStyle={{
                                fontSize: 20,
                                fontStyle: 'italic',
                                fontFamily: 'Georgia'
                            }}
                                title={
                                <div style={{display:'flex', alignItems: 'stretch'}}>
                                    <Col span={22}>
                                        {this.state.first_name + " " + this.state.last_name}
                                        {
                                            this.state.review_count > 10 &&
                                                <Icon style={{marginLeft: 10, color: '#DAA520'}} type="crown" theme="filled" />
                                        }
                                    </Col>
                                </div>
                                }
                            >
                                <p>
                                    <b><i>User Since: </i></b> 
                                    {   !this.state.private &&
                                        this.state.creation_date
                                    }
                                </p>

                                <p>
                                    <b><i>Review Count: </i></b> 
                                    {   !this.state.private &&
                                        this.state.review_count
                                    }
                                </p>

                                <p>
                                    <b><i>Favorite Genre: </i></b>
                                    {   !this.state.private &&
                                        this.state[this.state.genre]
                                    }
                                </p>


                            </Card>

                            {
                                this.state.private &&
                                <MDBMask className="flex-center" overlay="black-strong" >
                                <p className="black-text" style={{fontSize: 20,
                                    fontStyle: 'italic',
                                    fontFamily: 'Georgia'}}>Private</p>
                                </MDBMask>
                            }


                        </MDBView>
                        </MDBContainer>

                        </Col>

                    </Row>

                    <Row gutter={20} style={{ marginBottom: 16 }} type="flex" justify="center">
                        <Col span={22}>
                        <MDBContainer>
                        <MDBView>

                            <Card style={gridStyle} title="About Me">
                                <p>
                                    { !this.state.private &&
                                        this.state.bio
                                    }
                                </p>
                            </Card>
                            {
                                this.state.private &&
                                <MDBMask className="flex-center" overlay="black-strong" >
                                <p className="black-text" style={{fontSize: 20,
                                    fontStyle: 'italic',
                                    fontFamily: 'Georgia'}}>Private</p>
                                </MDBMask>
                            }


                        </MDBView>
                        </MDBContainer>
                        </Col>
                    </Row>

                    <Row gutter={20} style={{ marginBottom: 16 }} type="flex" justify="center">
                        <Col span={11}>
                        <MDBContainer>
                        <MDBView >

                        <Card style={{height: 522}} title="Favorites">
                            {
                            !this.state.private &&
                                <List
                                pagination={{
                                    onChange: (page) => {
                                        console.log(page);
                                    },
                                    pageSize: 2,
                                }}
                                grid={{ gutter: 0, column: 2 }}
                                dataSource={this.state.books}
                                renderItem={item => (
                                    <List.Item>
                                        <Card
                                            hoverable
                                            style={{ width: 180 }}
                                            cover={<img alt={item.title} src={item.image_url} />}
                                        >
                                            <Card.Meta
                                            title={<a href={'/booklist/'+item.pk}><b>{item.title}</b></a>}

                                            />
                                        </Card>
                                    </List.Item>
                                )}
                                />
                            }

                        </Card>
                        {
                            this.state.private &&
                            <MDBMask className="flex-center" overlay="black-strong" >
                            <p className="black-text" style={{fontSize: 20,
                                fontStyle: 'italic',
                                fontFamily: 'Georgia'}}>Private</p>
                            </MDBMask>
                        }

                        </MDBView>
                        </MDBContainer>
                        </Col>

                        <Col span={11}>
                        <Card title="Following">
                        <List
                            pagination={{
                                onChange: (page) => {
                                    console.log(page);
                                },
                                pageSize: 2,
                            }}
                            grid={{ gutter: 0, column: 2 }}
                            dataSource={this.state.authors}
                            renderItem={item => (
                                <List.Item>
                                    <Card
                                        hoverable
                                        style={{ width: 180 }}
                                        cover={<img alt={item.name} src={item.image_url} />}
                                    >
                                        <Card.Meta
                                        title={<a href={'/authors/'+item.pk}><b>{item.name}</b></a>}
                                        />
                                    </Card>
                                </List.Item>
                            )}
                        />
                        </Card>
                        </Col>
                    </Row>
                </div>
        )
    }
}


export default ProfileLookup;
