import React from 'react';
import Books from '../components/Book';
import axios from 'axios';
import {List, Card, Row} from 'antd';

class Explore extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            FA: "Fantasy",
            NF: "Non Fiction",
            RO: "Romance",
            TR: "Thriller",
            MY: "Mystery",
            BI: "Biography",
            FI: "Fiction",
            SF: "Science Fiction",
            books: []
        };
    }

    componentDidMount = () => {
        axios.get("http://127.0.0.1:8000/library/booklist/").then(res => {
            this.setState({
                books: res.data
            });
        })
        .catch(error => console.log(error));
    }

    render() {
        return (
            <Row gutter={20} type="flex" justify="center">

                <List
                grid={{
                    gutter: 16, column: 4
                }}
                pagination={{
                onChange: (page) => {
                    console.log(page);
                },
                pageSize: 8,
                }}
                dataSource={this.state.books}

                renderItem={item => (
                    <List.Item>
                        <Card
                            hoverable
                            style={{ width: 240 }}
                            cover={<img alt={item.title} src={item.image_url} />}
                        >
                            <Card.Meta
                            title={<a href={'/booklist/'+item.pk}><b>{item.title }</b></a>}
                            />
                        </Card>
                        <Row style={{ marginBottom: 8 }}></Row>
                    </List.Item>
                )}
            />

        </Row>
        )
    }
}

export default Explore;
