import React from 'react';
import { List, Avatar, Icon, Card } from 'antd';

const IconText = ({ type, text }) => (
<span>
    <Icon type={type} style={{ marginRight: 8 }} />
    {text}
</span>
);

const Books = (props) => {
    console.log(props.data);
    return (
        <List
            grid={{
                gutter: 16, xs: 1, sm: 2, md: 4, lg: 4, xl: 6, xxl: 3,
            }}
            pagination={{
            onChange: (page) => {
                console.log(page);
            },
            pageSize: 12,
            }}
            dataSource={props.data}

            renderItem={item => (
                <List.Item>
                    <Card
                        hoverable
                        style={{ width: 180 }}
                        cover={<img alt={item.name} src={item.image_url} />}
                    >
                        <Card.Meta
                        title={<a href={'/booklist/'+item.pk}><b>{item.name}</b></a>}
                        />
                    </Card>
                </List.Item>
            )}
        />
    )
}

export default Books;
